import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

const Homepage = () => {
  const [trends, setTrends] = useState([]);
  const [hashtags, setHashtags] = useState([]);
  const [stats, setStats] = useState({});
  const [visibleElements, setVisibleElements] = useState(new Set());
  const observerRef = useRef();

  useEffect(() => {
    // Mock data
    setTrends([
      { id: 1, title: 'Sustainable Products', engagement: '2.3M', growth: '+15%' },
      { id: 2, title: 'Local Malaysian Brands', engagement: '1.8M', growth: '+22%' },
      { id: 3, title: 'Tech Gadgets', engagement: '1.5M', growth: '+8%' },
      { id: 4, title: 'Food & Beverage', engagement: '3.1M', growth: '+12%' }
    ]);

    setHashtags([
      '#MalaysianMade', '#SustainableLiving', '#TechMalaysia', '#LocalBrand',
      '#EcoFriendly', '#Innovation', '#SmallBusiness', '#MadeInMalaysia'
    ]);

    setStats({
      totalUsers: '12.5K',
      campaignsCreated: '45.2K',
      avgEngagement: '23.8%',
      timeSaved: '2.1M hrs'
    });

    // Intersection Observer for scroll animations
    observerRef.current = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setVisibleElements(prev => new Set([...prev, entry.target.dataset.animate]));
          }
        });
      },
      { threshold: 0.1 }
    );

    // Observe all animated elements
    const elements = document.querySelectorAll('[data-animate]');
    elements.forEach(el => observerRef.current.observe(el));

    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
    };
  }, []);

  return (
    <div className="main-content">
      {/* Greeting Section */}
      <section style={{ marginBottom: '3rem', textAlign: 'center' }} data-animate="greeting" className="animate-on-scroll">
        <h1 style={{ 
          fontSize: '2.5rem', 
          marginBottom: '0.5rem', 
          color: 'white',
          animation: visibleElements.has('greeting') ? 'fadeInUp 0.8s ease-out' : 'none'
        }}>
          Welcome to nebula.ai
        </h1>
        <p style={{ 
          fontSize: '1.1rem', 
          color: 'rgba(255, 255, 255, 0.7)', 
          marginBottom: '2rem',
          animation: visibleElements.has('greeting') ? 'fadeInUp 0.8s ease-out 0.2s both' : 'none'
        }}>
          AI-powered marketing campaigns for Malaysian SMEs
        </p>

        {/* Quick Actions */}
        <div style={{ 
          display: 'flex', 
          gap: '1rem', 
          flexWrap: 'wrap', 
          marginBottom: '3rem',
          justifyContent: 'center',
          animation: visibleElements.has('greeting') ? 'fadeInUp 0.8s ease-out 0.4s both' : 'none'
        }}>
          <Link to="/register" className="btn btn-primary">
            🚀 Start Creating
          </Link>
          <Link to="/login" className="btn btn-secondary">
            📊 View Dashboard
          </Link>
          <button className="btn btn-secondary">
            📈 See Analytics
          </button>
        </div>
      </section>

      {/* Dashboard Overview */}
      <section style={{ marginBottom: '3rem' }} data-animate="dashboard" className="animate-on-scroll">
        <h2 style={{ 
          marginBottom: '2rem', 
          color: 'white', 
          textAlign: 'center',
          animation: visibleElements.has('dashboard') ? 'slideInLeft 0.6s ease-out' : 'none'
        }}>
          Platform Overview
        </h2>
        <div className="grid grid-2">
          {Object.entries(stats).map(([key, value], index) => (
            <div 
              key={key} 
              className="card"
              style={{
                animation: visibleElements.has('dashboard') ? `scaleIn 0.6s ease-out ${index * 0.1}s both` : 'none'
              }}
            >
              <h3 style={{ color: '#60a5fa', marginBottom: '0.5rem', textTransform: 'capitalize' }}>
                {key.replace(/([A-Z])/g, ' $1').trim()}
              </h3>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: 'white' }}>{value}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Statistics Graph */}
      <section style={{ marginBottom: '3rem' }} data-animate="graph" className="animate-on-scroll">
        <div className="card">
          <h2 style={{ 
            marginBottom: '2rem', 
            color: 'white', 
            textAlign: 'center',
            animation: visibleElements.has('graph') ? 'fadeInUp 0.6s ease-out' : 'none'
          }}>
            Campaign Performance
          </h2>
          <div style={{ 
            height: '300px', 
            background: 'rgba(59, 130, 246, 0.05)', 
            borderRadius: '8px', 
            position: 'relative', 
            overflow: 'hidden' 
          }}>
            {/* Mock Graph */}
            <div style={{ 
              position: 'absolute', 
              bottom: '20px', 
              left: '20px', 
              right: '20px', 
              display: 'flex', 
              alignItems: 'end', 
              gap: '10px', 
              height: '200px' 
            }}>
              {[65, 45, 80, 55, 90, 70, 85].map((height, index) => (
                <div 
                  key={index} 
                  style={{
                    flex: 1,
                    height: visibleElements.has('graph') ? `${height}%` : '0%',
                    background: `linear-gradient(to top, #3b82f6, #60a5fa)`,
                    borderRadius: '4px 4px 0 0',
                    boxShadow: '0 0 10px rgba(59, 130, 246, 0.3)',
                    transition: `height 1s ease-out ${index * 0.1}s`,
                    transformOrigin: 'bottom'
                  }} 
                />
              ))}
            </div>
            <div style={{ 
              position: 'absolute', 
              bottom: '0', 
              left: '20px', 
              right: '20px', 
              display: 'flex', 
              justifyContent: 'space-between', 
              color: 'rgba(255, 255, 255, 0.6)', 
              fontSize: '0.8rem' 
            }}>
              {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => (
                <span 
                  key={day}
                  style={{
                    opacity: visibleElements.has('graph') ? 1 : 0,
                    transition: `opacity 0.5s ease-out ${index * 0.1 + 0.5}s`
                  }}
                >
                  {day}
                </span>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Today's Trends */}
      <section style={{ marginBottom: '3rem' }} data-animate="trends" className="animate-on-scroll">
        <h2 style={{ 
          marginBottom: '2rem', 
          color: 'white', 
          textAlign: 'center',
          animation: visibleElements.has('trends') ? 'slideInRight 0.6s ease-out' : 'none'
        }}>
          Today's Ad Trends in Malaysia
        </h2>
        <div className="grid grid-2">
          {trends.map((trend, index) => (
            <div 
              key={trend.id} 
              className="card"
              style={{
                animation: visibleElements.has('trends') ? `fadeInUp 0.6s ease-out ${index * 0.1}s both` : 'none'
              }}
            >
              <h3 style={{ color: '#60a5fa', marginBottom: '0.5rem' }}>{trend.title}</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span style={{ color: 'rgba(255, 255, 255, 0.6)' }}>Engagement: {trend.engagement}</span>
                <span style={{ color: '#22c55e', fontWeight: '600' }}>{trend.growth}</span>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Viral Hashtags */}
      <section style={{ marginBottom: '3rem' }} data-animate="hashtags" className="animate-on-scroll">
        <h2 style={{ 
          marginBottom: '2rem', 
          color: 'white', 
          textAlign: 'center',
          animation: visibleElements.has('hashtags') ? 'fadeInUp 0.6s ease-out' : 'none'
        }}>
          Trending Hashtags
        </h2>
        <div className="card">
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.5rem', justifyContent: 'center' }}>
            {hashtags.map((hashtag, index) => (
              <span 
                key={index} 
                style={{ 
                  background: 'linear-gradient(45deg, #3b82f6, #1d4ed8)', 
                  color: 'white', 
                  padding: '0.5rem 1rem', 
                  borderRadius: '20px', 
                  fontSize: '0.9rem',
                  fontWeight: '500',
                  boxShadow: '0 0 15px rgba(59, 130, 246, 0.3)',
                  opacity: visibleElements.has('hashtags') ? 1 : 0,
                  transform: visibleElements.has('hashtags') ? 'translateY(0)' : 'translateY(20px)',
                  transition: `all 0.5s ease-out ${index * 0.1}s`
                }}
              >
                {hashtag}
              </span>
            ))}
          </div>
        </div>
      </section>

      {/* Features */}
      <section data-animate="features" className="animate-on-scroll">
        <h2 style={{ 
          marginBottom: '2rem', 
          color: 'white', 
          textAlign: 'center',
          animation: visibleElements.has('features') ? 'fadeInUp 0.6s ease-out' : 'none'
        }}>
          Why Choose nebula.ai?
        </h2>
        <div className="grid grid-3">
          {[
            { icon: '🚀', title: 'AI-Powered', desc: 'Generate professional content using advanced AI models trained for Malaysian market' },
            { icon: '⚡', title: 'Lightning Fast', desc: 'Create complete campaigns in minutes, not hours or days' },
            { icon: '🎯', title: 'Malaysian-Focused', desc: 'Optimized for local trends, language, and cultural preferences' }
          ].map((feature, index) => (
            <div 
              key={feature.title}
              className="card" 
              style={{ 
                textAlign: 'center',
                animation: visibleElements.has('features') ? `scaleIn 0.6s ease-out ${index * 0.2}s both` : 'none'
              }}
            >
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>{feature.icon}</div>
              <h3 style={{ marginBottom: '1rem', color: '#60a5fa' }}>{feature.title}</h3>
              <p style={{ color: 'rgba(255, 255, 255, 0.7)' }}>{feature.desc}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Homepage;
